import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import HomePage from './components/HomePage';
import InterviewsPage from './components/InterviewsPage';
import './App.css';
import AITestingTools from './components/AITestingTools';
import TestingProjects from './components/TestingProjects';
import About from './components/About';
import ModalLoginRegister from './components/ModalLoginRegister';
import 'antd/dist/reset.css';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  // 在组件加载时检查登录状态
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      // 可以从 localStorage 获取用户信息，或者调用后端接口获取
      const nickname = localStorage.getItem('nickname');
      setUserInfo({ nickname });
    }
  }, []);

  const handleLogin = (userData) => {
    setIsLoggedIn(true);
    setUserInfo(userData);
    setShowModal(false);
    localStorage.setItem('nickname', userData.nickname);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserInfo(null);
    localStorage.removeItem('token');
    localStorage.removeItem('nickname');
  };

  return (
    <Router>
      <div className="App">
        <Header openModal={() => setShowModal(true)} />
        {showModal && !isLoggedIn && (
          <ModalLoginRegister 
            onClose={() => setShowModal(false)} 
            onLoginSuccess={handleLogin}
          />
        )}
        <main className="App-content">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/interviews" element={<InterviewsPage />} />
            <Route path="/ai-enhanced" element={<AITestingTools />} />
            <Route path="/projects" element={<TestingProjects />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
