import React, { useState } from 'react';
import './ModalLoginRegister.css';

const ModalLoginRegister = ({ onClose }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [nickname, setNickname] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // 处理登录或注册逻辑
    console.log(isLogin ? 'Login' : 'Register', { nickname, phone, password });
  };

  const toggleMode = () => setIsLogin(!isLogin);

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>×</button>
        <div className="left-panel">
          <h2>欢迎来到kristenwang</h2>
          <ul>
            <li>8年+大厂架构经验分享</li>
            <li>全面深入50W+年薪技能</li>
            <li>构建AI赋能测试能力</li>
            <li>架构面试干货，每日更新</li>
            <li>大厂面试经验，定期分享</li>
          </ul>
        </div>
        <div className="right-panel">
          <h2>kristenwang</h2>
          <p className="subtitle">{isLogin ? '登录' : '注册'}</p>
          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <input
                type="text"
                placeholder="可爱的昵称"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
              />
            )}
            <input
              type="text"
              placeholder="登录手机号"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
            <input
              type="password"
              placeholder="密码"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {!isLogin && <span className="note">用作登录</span>}
            <button type="submit" className="submit-button">
              {isLogin ? '快速登录' : '快速注册'}
            </button>
          </form>
          <div className="login-options">
            <a href="#" onClick={(e) => { e.preventDefault(); }}>忘记密码？</a>
            <a href="#" onClick={(e) => { e.preventDefault(); toggleMode(); }}>
              {isLogin ? '新用户? 注册' : '已有账号? 登录'}
            </a>
          </div>
          {isLogin && (
            <div className="wechat-login">
              <span>推荐登录：</span>
              <button className="wechat-button">
                <span className="wechat-icon">W</span> 微信
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ModalLoginRegister;
