import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import HomePage from './components/HomePage';
import InterviewsPage from './components/InterviewsPage';
import './App.css';
import AITestingTools from './components/AITestingTools';
import TestingProjects from './components/TestingProjects';
import About from './components/About';
import ModalLoginRegister from './components/ModalLoginRegister';  // 引入 ModalLoginRegister 组件

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false); // 初始值为 false，模态框默认隐藏

  // 打开模态框
  const handleLoginClick = () => {
    setIsModalOpen(true);
  };

  // 关闭模态框
  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <main className="App-content">
          {/* 登录按钮 */}
          <button onClick={handleLoginClick} className="login-btn">登录</button>

          {/* 模态框 */}
          <ModalLoginRegister isOpen={isModalOpen} onClose={handleCloseModal} />

          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/interviews" element={<InterviewsPage />} />
            <Route path="/ai-enhanced" element={<AITestingTools />} />
            <Route path="/projects" element={<TestingProjects />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
