import React from 'react';
import './InterviewsPage.css';

function InterviewsPage() {
  const interviewTopics = [
    {
      title: "大厂必问一：设计测试用例",
      points: [
        "测试理论（黑盒测试、白盒测试、灰盒测试）",
        "测试用例设计方法（等价类划分、边界值分析、因果图等）",
        "测试流程和测试计划制定",
        "接口测试用例怎么设计",
        "给一个具体场景，设计测试用例",
        "......"
      ]
    },
    {
      title: "大厂必问二：代码基础知识",
      points: [
        "代码规范指南",
        "进程线程和协程",
        "常用包以及函数用法，装饰器",
        "可变对象和不可变对象区别",
        "如何理解代码的可读性和可扩展性",
        "......"
      ]
    },
    {
      title: "大厂必问三：自动化测试",
      points: [
        "介绍公司的自动化测试平台",
        "自动化测试异步场景用例",
        "如何提高自动化测试的稳定性",
        "持续集成/持续部署（CI/CD）工具（如 Jenkins）",
        "......"
      ]
    },
    {
      title: "大厂必问四：测试工具",
      points: [
        "SQL 基础",
        "数据库测试方法"
      ]
    },
    {
      title: "大厂必问五：性能测试",
      points: [
        "性能测试主要关注的指标",
        "网络协议（HTTP/HTTPS、TCP/IP）",
        "......"
      ]
    },
    {
      title: "大厂必问四：测试工具",
      points: [
        "SQL 基础",
        "数据库测试方法"
      ]
    },
    {
      title: "大厂必问五：性能测试",
      points: [
        "Linux 基本命令",
        "网络协议（HTTP/HTTPS、TCP/IP）"
      ]
    },
    {
      title: "大厂必问六：测试质量保证",
      points: [
        "如何保证测试的稳定性",
        "如何通过测试保证线上稳定性",
        "问题分析与解决能力",
        "时间管理和优先级设定"
      ]
    },
    {
      title: "大厂必问七：项目中印象深刻的bug",
      points: [
        "过往项目中遇到的挑战及解决方案",
        "测试策略制定经验",
        "自动化测试实践"
      ]
    },
    {
      title: "大厂必问八：如何与团队合作完成工作",
      points: [
        "软件开发方法论（敏捷、Scrum等）",
        "测试新趋势（AI在测试中的应用、持续测试等）"
      ]
    },
    {
      title: "质量意识",
      points: [
        "质量保证的理解",
        "如何提高产品质量的建议"
      ]
    }
  ];

  return (
    <div className="interviews-page">
      <h1>大厂软件测试面试要点</h1>
      <div className="interview-topics-list">
        {interviewTopics.map((topic, index) => (
          <div key={index} className="topic-section">
            <h2>{topic.title}</h2>
            <ul>
              {topic.points.map((point, pointIndex) => (
                <li key={pointIndex}>{point}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default InterviewsPage;
